import Comment from 'pages/acticle/components/comment';

const CommentsList = ({
  slug,
  comments,
  isAuthor,
  doFetchComments
}) => {
  return (
    <div className='comments-list'>
      {comments.map((comment) => (
        <Comment
          key={comment.id}
          slug={slug}
          comment={comment}
          isAuthor={isAuthor}
          doFetchComments={doFetchComments}
        />
      ))}
    </div>
  );
};

export default CommentsList;